  <script>
    function fetchClientName(nitInput) {
      var clientNit = nitInput.value;
      var devolClientInput = nitInput.parentElement.nextElementSibling.querySelector('input[name="devolClient[]"]');

      // Realiza una llamada AJAX al servidor para obtener el nombre del cliente
      $.ajax({
        url: 'traercliente.php',
        type: 'GET',
        data: {
          nit: clientNit
        },
        success: function(response) {
          var data = JSON.parse(response);
          devolClientInput.value = data.clientName.toUpperCase();
        },
        error: function() {
          console.error('Error fetching client name');
        }
      });
    }
  </script>

