  <script>
    //Estructura de calculo de temperatura promedio para el modal/formulario control de proceso enfriamiento de canal.        
    function calcular() {

      let suma = 0;
      let cantidad = 0;

      const inputs = document.querySelectorAll('.controlInput');

      inputs.forEach(function(input) {
        const valor = parseFloat(input.value);

        if (!isNaN(valor)) {
          suma += valor;
          cantidad++;
        }
      });

      let promedio = (cantidad > 0) ? suma / cantidad : 0;

      document.getElementById('tempPromedio').value = promedio.toFixed(2);
    }

    //calculo de porcentaje para el modal/formulario control de proceso enfriamiento canal.

    function porcentCanal() {
      let total = 100; // Porcentaje inicial
      const inputs = document.querySelectorAll('.controlInput');

      inputs.forEach(input => {
        let valor = input.value.trim(); // Obtenemos el valor del input y lo limpiamos

        // Si el valor está vacío o no es un número, lo asignamos como 0
        if (valor === '' || isNaN(valor)) {
          valor = 0;
        } else {
          valor = parseFloat(valor); // Convertimos el valor a número
        }

        // Si el valor es mayor a 3.8, restamos 1% al total
        if (valor > 3.8) {
          total -= 1;
        }
      });

      // Actualizamos el valor del input de resultado con el valor calculado.
      const resultado = document.getElementById('porcenCumplimiento');
      if (resultado) {

        resultado.value = total.toFixed(1); // Asignamos el valor con 1 decimal
      } else {

      }
    }
    // Registrar el evento 'input' para cada uno de los inputs de clase 'controlInput'
    const inputs = document.querySelectorAll('.controlInput');
    inputs.forEach(input => {
      input.addEventListener('input', porcentCanal);
    });
  </script>

