          document.getElementById('escanerMaterial').style.display = 'block';
          document.getElementById('btnEscanerCerrar').style.display = 'inline-block';
          scanner2.start(selectedCam);
        } else {
          alert('No se encontraron cámaras.');
        }
      }).catch(function(e) {
        console.error("Error al acceder a la cámara:", e);
        alert("Error: " + e.message);
      });
    });

    // Botón para cerrar el escáner
    document.getElementById('btnEscanerCerrar').addEventListener('click', function() {
      scanner2.stop();
      document.getElementById('escanerMaterial').style.display = 'none';
      this.style.display = 'none';
    });

    function camMaterial(qrCedula) {
      console.log("Buscando datos para:", qrCedula); // Confirmación de ejecución

      $.ajax({
        url: "../models/controlCam.php", // Asegúrate de que la ruta sea correcta
        type: "POST",
        dataType: "json",
        data: {
          qrCedula: qrCedula
        },
        success: function(data) {
          console.log("Respuesta del servidor:", data); // Verifica en consola la respuesta
          if (data.length > 0) {
            const empleado = data[0];
            $("#inpNombreMaterial").val(empleado.nombre);
            $("#QRcedulaLista").val(empleado.cedula); // Asegúrate que este ID esté bien escrito
          } else {
            alert("No se encontró ningún empleado con esa cédula.");
          }
        },
        error: function(xhr, status, error) {
          console.error("Error en AJAX:", status, error);
          alert("Ocurrió un error al buscar el empleado.");
        }
      });
    }
  </script>

</body>

</html>